	//========================================================
	// 전역변수
	//========================================================
	var userColor = "3";
	var menuSe = "G1";

	//========================================================
	// 화면 LOAD
	//========================================================
	scwin.onpageload = function() {
		$(function(){
			$(".w2group.shortcut").mouseover(function(){
				$(".header").find(".btn_box").slideDown();
			});

			$(".btn_box").mouseleave(function(){
				$(".btn_box").slideUp();
			})
			$(".w2group.set,.btn_mypage").mouseover(function(){
				$(".btn_box").slideUp()
			})
		});
	};

	scwin.onpageunload = function() {

	};

	//========================================================
	// 화면 이벤트
	//========================================================
	scwin.ChangepwdPopup_onclick = function(e) {
		requires("uiplugin.popup"); 
		var winWid = $(window).width();
		var winHei = $(window).height();
		var popWid = 500;
		var popHei = 310;
		var sumLeft = (winWid - popWid) / 2;
		var sumTop = (winHei - popHei) / 2;

		var opts = {
			"id" : "chgPassword",
			"type" : "wframePopup",	// iframePopup, wframePopup
			"width" : popWid + "px",
			"height" : popHei + "px",
			"top" : sumTop, 
			"left" : sumLeft,
			"popupName" : "비밀번호변경",
			"modal" : true, 
			"useIFrame" : false,
			"title" : "focusTest",
			"useATagBtn" : true,
//			"frameMode" : "wframe"
		};
//		WebSquare.util.openPopup("/cm/xml/settingPassword.xml", opts);
		$p.openPopup("/ui/system/popup/settingPassword.xml", opts);
	};

	scwin.SettingPopup_onclick = function(e) {console.log($p.parent().userColor);
		requires("uiplugin.popup"); 
		var winWid = $(window).width();
		var winHei = $(window).height();
		var popWid = 590;
		var popHei = 200;
		var sumLeft = (winWid - popWid) / 2;
		var sumTop = (winHei - popHei) / 2;
		/*
		var opts = {
			"id" : "popup_window_wframe",
			"type" : "wframePopup",
			"width" : popWid + "px",
			"height" : popHei + "px",
			"top" : sumTop, 
			"left" : sumLeft,
			"popupName" : "사용자 Color 선택",
			"modal" : true, 
			"useIFrame" : false,
			"title" : "focusTest",
			"useATagBtn" : true,
	//		"frameMode" : "wframe"
		};*/
//		WebSquare.util.openPopup("/xml/userColor.xml", opts);
//		$p.openPopup("/ui/system/popup/userColor.xml", opts);
		var options = {
			id : "userSetting",
			title : "사용자 설정",
			url : "/ui/system/popup/userColor.xml",
			param : {
				   userColor : userColor
				 , menuSe : menuSe
			},
			width : popWid,
			height : popHei,
			top : sumTop,
			left : sumLeft,
			useControl : true,
			closeBtn : false,
		}
		_util.openPopup(options);
	};

	//========================================================
	// 화면별 사용자 정의 함수
	//========================================================
	scwin.fn_getMenuSe = function(){
		return menuSe;
	};

