	scwin.fn_exe02_F0 = function() {
		_request.get("/template/multi_template_02_F0", {
			data : {
				normal : {
					   CD_Project : dlt_01.getCellData(0, "CD_Project")
				}
			},
			success : function(res) {
				dlt_02.setJSON(res.out);
				if(0 <= G_02_index) {
					gd_02.setFocusedCell(G_02_index, 0, false);
				} else {
					gd_02.setFocusedCell(0, 0, false);
				}
				for(var i=0; i<dlt_02.getRowCount(); i++){
					var ipType = "text";
					if(dlt_02.getCellData(i, "YN_File") == "Y"){
						ipType = "button";

						dlt_02.setCellData(i, "tg_file", "보기");
					}
					var info = {
						inputType : ipType
					}
					gd_02.setCellInputType(i, "tg_file", info);
				}

				dlt_02.modifyAllStatus("R");
			}
		})
	};

