	scwin.fn_selectValidation = function() {
		if(ipF_CD_Project.getValue() == "") {
			_util.alert("조회조건을 입력하세요.");
			ipF_CD_Project.focus();
			return false;
		}

		return true;
	};

	scwin.fn_newValidation = function() {

		return true;
	};

	scwin.fn_saveValidation = function() {
		var createdRows = dlt_01.getRowIndexByStatus("C").length;
		var updatedRows = dlt_01.getRowIndexByStatus("U").length;
		var deletedRows = dlt_01.getRowIndexByStatus("D").length;

		if(createdRows + updatedRows + deletedRows == 0){
			return false;
		}

		// 필수 값 체크
		var tmpData = dlt_01.getAllJSON();
		for(i=0; i<tmpData.length; i++){
			if(tmpData[i].rowStatus == "C" || tmpData[i].rowStatus == "U"){
				if(tmpData[i].ID_Sabun == ""){
					_util.alert("사원번호은(는) 필수항목입니다.", "error");
					gd_01.setFocusedCell(i, 0, true);
					return false;
				}
				if(tmpData[i].CD_Vacation_GB == ""){
					_util.alert("경조구분은(는) 필수항목입니다.", "error");
					gd_01.setFocusedCell(i, 0, true);
					return false;
				}
				if(tmpData[i].DT_Vacation_Day == ""){
					_util.alert("발생일자은(는) 필수항목입니다.", "error");
					gd_01.setFocusedCell(i, 0, true);
					return false;
				}
				if(tmpData[i].DT_Give == ""){
					_util.alert("경조비은(는) 필수항목입니다.", "error");
					gd_01.setFocusedCell(i, 0, true);
					return false;
				}
			}
		}

		return true;
	};

	scwin.fn_deleteValidation = function() {
		if(dlt_01.getRowCount() == 0){
			return false;
		}

//		if (G_01_index < 0) {
//			_util.alert("삭제할 항목을 선택해 주십시오.", "error");
//			return false;
//		}

		return true;
	}

	scwin.fn_newValidation_02 = function() {
		var cdProject = dlt_01.getCellData(0, "CD_Project");
		var rowP = dlt_01.getRowStatus(0);

		if(cdProject == "" || rowP == "C"){
			_util.alert("계약정보를 등록 후 사용할 수 있습니다.", "error");
			return false;
		}

		return true;
	};

	scwin.fn_deleteValidation_02 = function() {
		if(dlt_02.getRowCount() == 0){
			return false;
		}

		if (G_02_index < 0) {
			_util.alert("삭제할 항목을 선택해 주십시오.", "error");
			return false;
		}

		return true;
	}

	scwin.fn_searchDeptClient = function(dsClient) {
		var winWid = $(window).width();
		var winHei = $(window).height();
		var popWid = 820;
		var popHei = 830;
		var sumLeft = (winWid - popWid) / 2;
		var sumTop = (winHei - popHei) / 2;
		var options = {
			id : "searchDeptClient",
			title : "부서 찾기",
			url : "/ui/system/popup/searchDept_p.xml",
			param : {
				DS_Client : dsClient
			},
			width : popWid,
			height : popHei,
			top : sumTop,
			left : sumLeft
		}
		_util.openPopup(options);
	};

