	//팝업정보삽입
	_util.addCallbackListener({
		searchDeptClient : function(item) {
			dlt_01.setCellData(0, "CD_Client", item.CD_Jojik);
			dlt_01.setCellData(0, "DS_Client", item.DS_JojikName);
		},
		searchDeptJojik : function(item) {
			dlt_01.setCellData(0, "CD_Jojik", item.CD_Jojik);
			dlt_01.setCellData(0, "DS_Jojik", item.DS_JojikName);
		},
		searchDeptSujuJojik : function(item) {
			dlt_01.setCellData(0, "CD_SujuJojik", item.CD_Jojik);
			dlt_01.setCellData(0, "DS_SujuJojik", item.DS_JojikName);
		}
	});
