head.script:

	scwin.onpageload = function() {
		  $(".btn_round.arrw").click(function(){
		  $(".btn_round.arrw").toggleClass("on");
		  $(".shbox.more .shbox_inner .tb").toggleClass("row1");
 		  });

 		 $(".accr_wrap .items .tit_box a").click(function(){
		 $(this).parent().parent().toggleClass("on");  
		 });

		 pageList1.setCount(100);
		 $(".btn_fav").click(function(){
		 $(".btn_fav").toggleClass("on");
		 });
	};

	scwin.openPopup_onclick = function(e) {
		requires("uiplugin.popup"); 
		var winWid = $(window).width();
		var winHei = $(window).height();
		var popWid = 500;
		var popHei = 250;
		var sumLeft = (winWid - popWid) / 2;
		var sumTop = (winHei - popHei) / 2;

		var opts = {
		"id" : "popup_window_wframe",
		"type" : "litewindow",
		"width" : popWid + "px",
		"height" : popHei + "px",
		"top" : sumTop, 
		"left" : sumLeft,
		"popupName" : "메뉴그룹명",
		"modal" : true, 
		"useIFrame" : false,
		"title" : "focusTest",
		"useATagBtn" : true,
		"frameMode" : "wframe"
		};
		WebSquare.util.openPopup("/xml/settingPop.xml", opts);
	};

	scwin.onpageunload = function() {

	};
